import { Component, OnInit } from '@angular/core';
import { TeamService } from '../services/team.service';


@Component({
  selector: 'app-team',
  templateUrl: './team.page.html',
  styleUrls: ['./team.page.scss'],
})
export class TeamPage implements OnInit {

  teams:  any = [];

  constructor(private teamService: TeamService) { }

  ngOnInit() {
    this.getAllBicycles();
  }

  getAllBicycles() {
    this.teamService.getTeams().subscribe(response => {
      this.teams = response;
    })
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>team</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">team</ion-title>
    </ion-toolbar>
  </ion-header>
</ion-content>

<ion-list>
  <ion-item *ngFor="let t of teams">
    <ion-label class="list">
      <h1>{{t.name}}</h1>
      <p>{{t.region}}</p>
      <p>{{t.position}}</p>
      <ion-icon name="create-outline" slot="end" (click)="gotoEdit()"></ion-icon>
      <ion-icon name="trash" slot="end" (click)="deleteChamp(c.id)"></ion-icon>
  </ion-label>
    </ion-item>
