import { Component, OnInit } from '@angular/core';
import { TeamService } from '../services/team.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-team',
  templateUrl: './team.page.html',
  styleUrls: ['./team.page.scss'],
})
export class TeamPage implements OnInit {

  menuType: string = 'push';

  teams:  any = [];

  constructor(private teamService: TeamService, private router: Router) { }

  ngOnInit() {
    this.getAllTeams();
  }

  getAllTeams() {
    this.teamService.getTeams().subscribe(response => {
      this.teams = response;
    });
  }

  deleteTeam(id: number) {
    this.teamService.delete(id).subscribe(response => {
      this.getAllTeams();
    });
  }

  goToHome() {
    this.router.navigateByUrl("/home");
  }

  goToEdit(team: any) {
    this.router.navigate(['/team-update', team.id]);
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Team List</ion-title>
  </ion-toolbar>
</ion-header>

<ion-menu [type]="menuType" content-id="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menu Content</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-content class="ion-padding">
    <ion-menu-toggle>
      <ion-button>Close Menu</ion-button>
    </ion-menu-toggle>
  </ion-content>
</ion-menu>
<div class="ion-page" id="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menu</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-content class="ion-padding">
    <ion-list [inset]="true">
      <ion-item *ngFor="let t of teams">
        <ion-label class="list">
          <h1>{{t.name}}</h1>
          <p>{{t.region}}</p>
          <p>{{t.position}}</p>
          <ion-button fill="outline" (click)="deleteTeam(t.id)" color="danger">
            Delete
            <ion-icon name="trash-outline"></ion-icon>
          </ion-button>
          <ion-button fill="outline" (click)="goToEdit(t)" color="success">
            Edit
            <ion-icon name="create-outline"></ion-icon>
          </ion-button>
        </ion-label>
      </ion-item>
    </ion-list>
    <br />
    <ion-menu-toggle>
      <ion-button>Open Menu</ion-button>
    </ion-menu-toggle>
  </ion-content>
</div>
