import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { TeamService } from '../services/team.service';
import { Router, ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-team-update',
  templateUrl: './team-update.page.html',
  styleUrls: ['./team-update.page.scss'],
})
export class TeamUpdatePage implements OnInit {

  teamUpdateForm: FormGroup;
  teamToUpdate: any;

  constructor(public fb: FormBuilder, 
    private teamService: TeamService,
    private router: Router,
    private route: ActivatedRoute) {

  }

  ngOnInit() {
    this.route.params.subscribe(params => {
      const id = params['id'];

      this.teamService.getOneTeam(id).subscribe((team: any) => {
        this.teamToUpdate = team;
        this.teamUpdateForm.setValue({
          name: team.name,
          region: team.region,
          position: team.position
        })
      })
    });

    this.teamUpdateForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      region: ['', [Validators.required, Validators.minLength(3)]],
      position: ['', [Validators.required, Validators.minLength(1)]]
    });

  }

  updateTeam() {
    if (this.teamUpdateForm.valid && this.teamToUpdate) {
      const { name, region, position, id } = this.teamToUpdate;
      const updatedTeam = {
        name: this.teamUpdateForm.value.name, 
        region: this.teamUpdateForm.value.region, 
        position: this.teamUpdateForm. value.position
      };

      this.teamService.update(id, updatedTeam).subscribe(response => {
        this.teamToUpdate = null;
        this.goToTeams();
      });
    }
  }

  goToHome() {
    window.location.href = "/home";
  }

  goToTeams() {
    window.location.href = "/teams";
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Update the Team</ion-title>
  </ion-toolbar>
</ion-header>

<ion-menu [type]="menuList" content-id="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menu Content</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-content class="ion-padding">
    <ion-menu-toggle>
      <ion-button (click)="goToTeams()" color="success">
        Home
        <ion-icon name="home"></ion-icon>
      </ion-button>
    </ion-menu-toggle>
    <ion-menu-toggle>
      <ion-button (click)="goToAddTeams()" color="success">
        List Teams
        <ion-icon name="add-circle-outline"></ion-icon>
      </ion-button>
    </ion-menu-toggle>
    <ion-menu-toggle>
      <ion-button color="medium">Close Menu</ion-button>
    </ion-menu-toggle>
  </ion-content>
</ion-menu>
<div class="ion-page" id="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menu</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-content class="ion-padding">
    <ion-list [inset]="true">
      <ion-item *ngFor="let t of teams">
        <ion-label class="list">
          <h1>{{t.name}}</h1>
          <p>{{t.region}}</p>
          <p>{{t.position}}</p>
          <ion-button fill="outline" (click)="deleteTeam(t.id)" color="danger">
            Delete
            <ion-icon name="trash-outline"></ion-icon>
          </ion-button>
          <ion-button fill="outline" (click)="goToEdit(t)" color="success">
            Edit
            <ion-icon name="create-outline"></ion-icon>
          </ion-button>
        </ion-label>
      </ion-item>
    </ion-list>
    <br />
    <ion-menu-toggle>
      <ion-button color="medium">Open Menu</ion-button>
    </ion-menu-toggle>
  </ion-content>
</div>












<ion-content>
  <form [formGroup]="teamUpdateForm" (ngSubmit)="updateTeam()" novalidate>
    <ion-item lines="full">
      <ion-label position="floating">Name</ion-label>
      <ion-input formControlName="name" type="text" required></ion-input>
    </ion-item>

    <ion-item lines="full">
      <ion-label position="floating">Region</ion-label>
      <ion-input formControlName="region" type="text" required>
      </ion-input>
    </ion-item>

    <ion-item>
      <ion-label position="floating">Position</ion-label>
      <ion-input formControlName="position" type="number" required>
      </ion-input>
    </ion-item>

    <ion-row>
      <ion-col>
        <ion-button type="submit" color="primary" shape="full" expand="block">
          Update Team
        </ion-button>
      </ion-col>
    </ion-row>
  </form>
</ion-content>
