import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { TeamService } from '../services/team.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-team-update',
  templateUrl: './team-update.page.html',
  styleUrls: ['./team-update.page.scss'],
})
export class TeamUpdatePage implements OnInit {

  teamUpdateForm: FormGroup;

  constructor(public fb: FormBuilder, 
    private teamService: TeamService,
    private router: Router) {

  }

  ngOnInit() {
    this.teamUpdateForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      region: ['', [Validators.required, Validators.minLength(3)]],
      position: ['', [Validators.required, Validators.minLength(1)]]
    });
  }

  updateTeam(id: number) {
    if (this.teamUpdateForm.valid) {
      const nameControl = this.teamUpdateForm.get('name');
      const regionControl = this.teamUpdateForm.get('region');
      const positionControl = this.teamUpdateForm.get('position');

      if (nameControl && regionControl && positionControl) {
        const name = nameControl.value;
        const region = regionControl.value;
        const position = positionControl.value;

        this.teamService.update(id, { name, region, position }).subscribe(response => {
          this.teamUpdateForm.reset();
        });
      }
    }
  }

}
